import './App.css';
import { io } from 'socket.io-client';
import { useState, useEffect, useRef } from 'react';


const socket = io('http://localhost:3001'); 

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [mensajes, setMensajes] = useState([]);
  const [nuevoMensaje, setNuevoMensaje] = useState('');
  const [usuario, setUsuario] = useState('');
  const inputRef = useRef(null);

  // Login simple al conectar
  useEffect(() => {
    const nombre = prompt('驴Cu谩l es tu nombre?') || 'An贸nimo';
    setUsuario(nombre.trim() || 'An贸nimo');

    socket.on('connect', () => {
      setIsConnected(true);
      // Enviamos el login al servidor
      socket.emit('login', nombre.trim() || 'An贸nimo');
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
    });

    // 隆隆隆ESCUCHAMOS EL EVENTO CORRECTO QUE USA EL SERVIDOR!!!
    socket.on('chat message', (msg) => {
      setMensajes(prev => [...prev, msg]);
    });

    // Mensajes de sistema
    socket.on('user_joined', (data) => {
      setMensajes(prev => [...prev, { username: 'Sistema', text: `${data.username} se conect贸`, time: data.timestamp }]);
    });

    socket.on('user_left', (data) => {
      setMensajes(prev => [...prev, { username: 'Sistema', text: `${data.username} se desconect贸`, time: new Date().toLocaleTimeString() }]);
    });

    // Cargar mensajes antiguos
    socket.on('load_messages', (historial) => {
      setMensajes(historial);
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('chat message');
      socket.off('user_joined');
      socket.off('user_left');
      socket.off('load_messages');
    };
  }, []);

  const enviarMensaje = (e) => {
    e.preventDefault();

    if (!nuevoMensaje.trim()) return;


    socket.emit('chatmessage', nuevoMensaje.trim());
    //setNuevoMensaje('');
    inputRef.current?.focus();
  };

  return (
    <div className="App">
      <div className="header">
        <h2>Chat en Vivo {isConnected ? '' : ''}</h2>
        <p>Bienvenido, <strong>{usuario}</strong></p>
      </div>

      <div className="mensajes">
        {mensajes.map((msg, i) => (
          <div
            key={i}
            className={`mensaje ${msg.username === usuario ? 'propio' : ''} ${msg.username === 'Sistema' ? 'sistema' : ''}`}
          >
            {msg.username !== 'Sistema' && <span className="usuario">{msg.username}</span>}
            <span className="hora">{msg.time || new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
            <p>{msg.text || msg.mensaje}</p>
          </div>
        ))}
      </div>

      <form onSubmit={enviarMensaje} className="input-area">
        <input
          ref={inputRef}
          type="text"
          value={nuevoMensaje}
          onChange={(e) => setNuevoMensaje(e.target.value)}
          placeholder="Escribe tu mensaje..."
          autoFocus
        />
        <button type="submit" disabled={isConnected}>
          Enviar
        </button>
      </form>
    </div>
  );
}

export default App;
