// src/App.jsx
import './App.css';
import { io } from 'socket.io-client';
import { useState, useEffect, useRef } from 'react';

const socket = io('http://localhost:3000'); // o el puerto que uses en tu server

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [nuevoMensaje, setNuevoMensaje] = useState('');
  const [mensajes, setMensajes] = useState([]);
  const mensajesEndRef = useRef(null);

  // Scroll automático al último mensaje
  useEffect(() => {
    mensajesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [mensajes]);

  useEffect(() => {
    // Conexión
    socket.on('connect', () => {
      setIsConnected(true);
      console.log('Conectado al servidor');
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
      console.log('Desconectado del servidor');
    });

    // Recibir mensajes del servidor
    socket.on('mensaje', (data) => {
      setMensajes((prev) => [...prev, data]);
    });

    // Limpieza al desmontar
    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('mensaje');
    };
  }, []); // Dependencias vacías = solo al montar

  const enviarMensaje = () => {
    if (!nuevoMensaje.trim()) return;

    const mensaje = {
      usuario: socket.id.slice(0, 8), // nombre corto y bonito
      mensaje: nuevoMensaje,
      hora: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    };

    socket.emit('mensaje', mensaje);
    setNuevoMensaje('');
  };

  return (
    <div className="App">
      <h2>{isConnected ? 'Conectado al servidor' : 'Desconectado del servidor'}</h2>

      <div className="mensajes-container">
        <ul>
          {mensajes.map((msg, index) => (
            <li key={index}>
              <strong>{msg.usuario}</strong> ({msg.hora}): {msg.mensaje}
            </li>
          ))}
        </ul>
        <div ref={mensajesEndRef} />
      </div>

      <div className="input-area">
        <input
          type="text"
          value={nuevoMensaje}
          onChange={(e) => setNuevoMensaje(e.target.value)}
          placeholder="Escribe un mensaje..."
          onKeyDown={(e) => e.key === 'Enter' && enviarMensaje()}
        />
        <button onClick={enviarMensaje}>Enviar</button>
      </div>
    </div>
  );
}

export default App;
