import './App.css';
import {io} from 'socket.io-client';
import { useState, useEffect } from 'react';

const socket = io('http://localhost:3000');

function App() {

  const [isConnected, setIsConnected] = useState();

  useEffect(() => { 
    socket.on('connect', () => {
      setIsConnected(true);
    });
   }, []);

  return (
    <div className="App">

    </div>
  );
}

export default App;
