import './App.css';
import {io} from 'socket.io-client';
import { useState, useEffect } from 'react';
import { ULMensajes, LIMessage } from './chat.jsx';
const socket = io('http://localhost:3000');

function App() {

  const [isConnected, setIsConnected] = useState(false);
  const [nuevoMensaje, setnuevoMensaje] = useState('');
  const [mensajes, setMensajes] = useState([]); 

  useEffect(() => { 
    socket.on('connect', () => {
      setIsConnected(true);
    });
    socket.on('disconnect', () => {
      setIsConnected(false);
    });
    socket.on('mensaje', (data) => {
      setMensajes(mensajes => [...mensajes, data]);
    });
    return () => {
      socket.off('connect');
     // socket.off('disconnect');
      socket.off('mensaje');
    };
  }, []);


  const enviarmensaje = () => {
    socket.emit('mensaje', { usuario: socket.id, mensaje: nuevoMensaje });
    setnuevoMensaje('');
  }

  return (
    <div className="App">
     <h2>{isConnected ? 'Conectado al servidor' : 'Desconectado del servidor'}</h2>
     <ULMensajes>     
        <LIMessage >Laura: Hola a todos! </LIMessage>     
</ULMensajes>
      <input type="text" onChange={(e) => setnuevoMensaje(e.target.value)} placeholder="Escribe un mensaje..." />
      <button onClick={enviarmensaje} >Enviar</button> 
    </div>
  );
}

export default App;
